import React from "react";
import EmployeList from "./components/EmployeList";

const employees = [
  {
    id: 1,
    name: "Goku",
    department: "Admin",
  },
  {
    id: 2,
    name: "Tony",
    department: "Engineering",
  },
  {
    id: 3,
    name: "Loki",
    department: "Marketing",
  },
];

const empList = employees.map((emp, index) => (
  <h3 onClick={() => showDep(emp.id)} key={index}>
    {emp.name}
  </h3>
));

function App() {
  return (
    <div className="container">
      <h1>Employee List</h1>
      {empList}
      <EmployeList list={employees} showDep={showDep} />
    </div>
  );
}

export default App;
